mapper.select:
SELECT 

FROM course a

WHERE a.id = #{id}

SELECT
FROM course a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.category_name LIKE '%'||#{categoryName}||'%' '%'+#{categoryName}+'%' concat('%',#{categoryName},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
