mapper.select:
SELECT 

FROM market a

WHERE a.id = #{id}

SELECT 

FROM market a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.recommend = #{recommend}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM market a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE market SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
