mapper.select:
SELECT 

FROM project a 

WHERE a.is_history = 1 and  a.id = #{id}

SELECT 

FROM project a 




		AND a.sn = #{num}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.tender_type = #{type}


		AND a.entrust_money BETWEEN #{beginEntrustMoney} AND #{endEntrustMoney}


		AND a.bid_money BETWEEN #{beginBidMoney} AND #{endBidMoney}


		AND a.opentender_time BETWEEN #{beginTenderOpenTime} AND #{endTenderOpenTime}


		AND a.bid_time BETWEEN #{beginBidTime} AND #{endBidTime}


		AND a.org_id = #{orgId}  

	  AND a.is_history = 1 



		ORDER BY ${page.orderBy}

SELECT 

FROM project a where a.is_history = 1 






		ORDER BY ${page.orderBy}

SELECT 

	FROM project a 

	WHERE DATE_FORMAT(a.bid_time, '%Y')  >=    DATE_FORMAT(now(), '%Y')-'3' AND  DATE_FORMAT(a.bid_time, '%Y')   <=    DATE_FORMAT(now(), '%Y')-'1'  AND a.is_history = 1 and  a.org_id = #{id}

SELECT 

	FROM project a 

	WHERE a.is_history = 1 and  a.org_id = #{id}
