mapper.select:
SELECT 

FROM org_check a

WHERE a.id = #{id}

SELECT 

FROM org_check a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.year = #{year}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}

SELECT 

FROM org_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE org_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
