mapper.select:
SELECT 

FROM pedlar a

WHERE a.id = #{id}

SELECT 

FROM pedlar a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.user_id = #{user.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pedlar a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pedlar SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
