mapper.select:
SELECT 

FROM cdkey a

WHERE a.id = #{id}

SELECT 

FROM cdkey a




		AND a.code LIKE 
			'%'||#{code}||'%'
			'%'+#{code}+'%'
			concat('%',#{code},'%')


		AND a.status = #{status}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.active_date BETWEEN #{beginActiveDate} AND #{endActiveDate}




		ORDER BY ${page.orderBy}

SELECT 

FROM cdkey a




		AND a.code LIKE 
			'%'||#{code}||'%'
			'%'+#{code}+'%'
			concat('%',#{code},'%')


		AND a.batch_id = #{batchId}


		AND a.status = #{status}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.active_date BETWEEN #{beginActiveDate} AND #{endActiveDate}




		ORDER BY ${page.orderBy}

SELECT 

FROM cdkey a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cdkey SET 	
	batch_id = #{batchId},
	code = #{code},
	status = #{status},
	active_date = #{activeDate}
WHERE id = #{id}
