mapper.select:
SELECT 

FROM users a

WHERE a.id = #{id}

SELECT 

FROM users a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.age BETWEEN #{beginAge} AND #{endAge}


		AND a.grade = #{grade}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM users a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE users SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
